import React, { useState, useEffect } from 'react';
import { groupsAPI } from '../services/api';

const AdminDashboard = ({ user }) => {
  const [stats, setStats] = useState({
    users: 0,
    groups: 0,
    events: 0,
    enquiries: 0
  });
  const [recentUsers, setRecentUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user && user.username === 'superadmin') {
      fetchAdminData();
    }
  }, [user]);

  const fetchAdminData = async () => {
    try {
      // This would need admin-specific API endpoints
      // For now, we'll show basic info
      setStats({
        users: 15,
        groups: 8,
        events: 23,
        enquiries: 12
      });
      
      setRecentUsers([
        { id: 1, name: 'John Doe', email: 'john@example.com', created: '2025-10-25' },
        { id: 2, name: 'Jane Smith', email: 'jane@example.com', created: '2025-10-24' },
        { id: 3, name: 'Bob Wilson', email: 'bob@example.com', created: '2025-10-23' }
      ]);
      
    } catch (error) {
      console.error('Error fetching admin data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (!user || user.username !== 'superadmin') {
    return (
      <div className="admin-access-denied">
        <h2>🚫 Access Denied</h2>
        <p>Super Admin access required.</p>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <div className="admin-header">
        <h1>🔧 Super Admin Dashboard</h1>
        <p>Welcome, {user.first_name}! You have full system access.</p>
      </div>

      <div className="admin-stats">
        <div className="stat-card admin">
          <div className="stat-icon">👥</div>
          <div className="stat-content">
            <h3>Total Users</h3>
            <p className="stat-number">{stats.users}</p>
          </div>
        </div>

        <div className="stat-card admin">
          <div className="stat-icon">🏠</div>
          <div className="stat-content">
            <h3>Total Groups</h3>
            <p className="stat-number">{stats.groups}</p>
          </div>
        </div>

        <div className="stat-card admin">
          <div className="stat-icon">📅</div>
          <div className="stat-content">
            <h3>Total Events</h3>
            <p className="stat-number">{stats.events}</p>
          </div>
        </div>

        <div className="stat-card admin">
          <div className="stat-icon">📝</div>
          <div className="stat-content">
            <h3>Enquiries</h3>
            <p className="stat-number">{stats.enquiries}</p>
          </div>
        </div>
      </div>

      <div className="admin-content">
        <div className="admin-section">
          <h3>🔧 Admin Tools</h3>
          <div className="admin-tools">
            <button className="admin-btn" onClick={() => alert('CLI Tool: Run backend/run_admin_cli.bat')}>
              💻 CLI Database Tool
            </button>
            <button className="admin-btn" onClick={() => alert('Email system configured')}>
              📧 Email System
            </button>
            <button className="admin-btn" onClick={() => alert('System logs available in backend console')}>
              📊 System Logs
            </button>
            <button className="admin-btn" onClick={() => alert('Database backup feature in CLI')}>
              💾 Backup Database
            </button>
          </div>
        </div>

        <div className="admin-section">
          <h3>👥 Recent Users</h3>
          <div className="recent-users-admin">
            {recentUsers.map(user => (
              <div key={user.id} className="user-card-admin">
                <div className="user-info">
                  <h4>{user.name}</h4>
                  <p>{user.email}</p>
                  <span className="user-date">Joined: {user.created}</span>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="admin-section">
          <h3>⚙️ System Information</h3>
          <div className="system-info">
            <div className="info-item">
              <strong>Super Admin:</strong> {user.username}
            </div>
            <div className="info-item">
              <strong>Email System:</strong> Configured (planmyouting@outlook.com)
            </div>
            <div className="info-item">
              <strong>CLI Access:</strong> Available via run_admin_cli.bat
            </div>
            <div className="info-item">
              <strong>Database:</strong> SQLite (plan_my_outings.db)
            </div>
            <div className="info-item">
              <strong>Notifications:</strong> Sent to planmyouting@outlook.com
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
