import React, { useState } from 'react';
import { contactAPI } from '../services/api';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    year_of_birth: '',
    message: ''
  });
  const [credentials, setCredentials] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const response = await contactAPI.submitEnquiry(formData);
      setCredentials(response.data.credentials);
      setFormData({
        first_name: '',
        last_name: '',
        email: '',
        year_of_birth: '',
        message: ''
      });
    } catch (error) {
      alert('Error submitting enquiry. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="contact-form">
      <h2>Contact Us</h2>
      <p>Fill out this form and we'll create your account automatically!</p>
      
      {credentials && (
        <div className="credentials-alert">
          <h3>Account Created Successfully!</h3>
          <p><strong>Username:</strong> {credentials.username}</p>
          <p><strong>Password:</strong> {credentials.password}</p>
          <p>Please save these credentials and use them to login.</p>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="text"
            name="first_name"
            placeholder="First Name"
            value={formData.first_name}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-group">
          <input
            type="text"
            name="last_name"
            placeholder="Last Name"
            value={formData.last_name}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-group">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-group">
          <input
            type="number"
            name="year_of_birth"
            placeholder="Year of Birth"
            min="1900"
            max="2023"
            value={formData.year_of_birth}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-group">
          <textarea
            name="message"
            placeholder="Your Message (Optional)"
            value={formData.message}
            onChange={handleChange}
            rows="4"
          />
        </div>
        
        <button type="submit" disabled={loading}>
          {loading ? 'Submitting...' : 'Submit & Create Account'}
        </button>
      </form>
    </div>
  );
};

export default ContactForm;
