import React, { useState, useEffect } from 'react';
import { groupsAPI } from '../services/api';

const Dashboard = ({ user }) => {
    const [groups, setGroups] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchGroups();
    }, []);

    const fetchGroups = async () => {
        try {
            const response = await groupsAPI.getGroups();
            setGroups(response.data);
        } catch (error) {
            console.error('Error fetching groups:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="dashboard">
            <h2>Welcome, {user.first_name}!</h2>

            <div className="dashboard-stats">
                <div className="stat-card">
                    <h3>Your Groups</h3>
                    <p className="stat-number">{groups.length}</p>
                </div>

                <div className="stat-card">
                    <h3>Active Events</h3>
                    <p className="stat-number">0</p>
                </div>

                <div className="stat-card">
                    <h3>Upcoming Plans</h3>
                    <p className="stat-number">0</p>
                </div>
            </div>

            <div className="recent-groups">
                <h3>Your Groups</h3>
                {loading ? (
                    <p>Loading groups...</p>
                ) : groups.length > 0 ? (
                    <div className="groups-list">
                        {groups.map(group => (
                            <div key={group.id} className="group-card">
                                <h4>{group.name}</h4>
                                <p>{group.description}</p>
                                <span className="member-count">{group.member_count} members</span>
                            </div>
                        ))}
                    </div>
                ) : (
                    <p>No groups yet. Create your first group to start planning!</p>
                )}
            </div>

            <div className="quick-actions">
                <h3>Quick Actions</h3>
                <div className="action-buttons">
                    <button onClick={() => window.location.href = '/groups'}>
                        Manage Groups
                    </button>
                    <button onClick={() => alert('Event planning coming soon!')}>
                        Plan New Event
                    </button>
                    <button onClick={() => alert('Voting system coming soon!')}>
                        View Polls
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
